import UIKit
import RealmSwift

class NotificationsPresenter: NotificationsPresenterProtocol {

    weak private var view: NotificationsViewProtocol?
    var interactor: NotificationsInteractorProtocol?
    private let router: NotificationsWireframeProtocol

    init(interface: NotificationsViewProtocol, interactor: NotificationsInteractorProtocol?, router: NotificationsWireframeProtocol) {
        self.view = interface
        self.interactor = interactor
        self.router = router
    }
    
    //MARK: - Firebase
    func getNotifications(userId: Int) {
        interactor?.getNotifications(userId: userId)
    }
    
    //MARK: - ShowPostDetail
    func showPostDetail(navController: UINavigationController?, post: PublicationRealm) {
        router.showPostDetail(navController: navController, post: post)
        
    }
    
    func didFinishGettingNotifications(data: [NotificationDocument]) {
        view?.didFinishGettingNotifications(data: data)
    }
    
    func writeIsViewed(documentId: String){
        interactor?.writeIsViewed(documentId: documentId)
    }
    

}
